mapper.select:
select 

from utxo
where tx_hash = #{txHash,jdbcType=VARCHAR}
  and tx_index = #{txIndex,jdbcType=INTEGER}

mapper.delete:
delete from utxo
where tx_hash = #{txHash,jdbcType=VARCHAR}
  and tx_index = #{txIndex,jdbcType=INTEGER}

mapper.insert:
insert into utxo (tx_hash, tx_index, address, 
  amount, lock_time, spend_tx_hash
  )
values (#{txHash,jdbcType=VARCHAR}, #{txIndex,jdbcType=INTEGER}, #{address,jdbcType=VARCHAR}, 
  #{amount,jdbcType=BIGINT}, #{lockTime,jdbcType=BIGINT}, #{spendTxHash,jdbcType=VARCHAR}
  )

mapper.update:
update utxo
set address = #{address,jdbcType=VARCHAR},
  amount = #{amount,jdbcType=BIGINT},
  lock_time = #{lockTime,jdbcType=BIGINT},
  spend_tx_hash = #{spendTxHash,jdbcType=VARCHAR}
where tx_hash = #{txHash,jdbcType=VARCHAR}
  and tx_index = #{txIndex,jdbcType=INTEGER}
