amount, lock_time, spend_tx_hash
)
values (#{hashIndex,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR},
#{amount,jdbcType=BIGINT}, #{lockTime,jdbcType=BIGINT}, #{spendTxHash,jdbcType=VARCHAR}
)

insert into utxo (hash_index, address,
amount, lock_time, spend_tx_hash
) values

  values (#{item.hashIndex,jdbcType=VARCHAR}, #{item.address,jdbcType=VARCHAR},
  #{item.amount,jdbcType=BIGINT}, #{item.lockTime,jdbcType=BIGINT}, #{item.spendTxHash,jdbcType=VARCHAR}
  )

mapper.update:
insert into utxo (hash_index, address,
amount, lock_time, spend_tx_hash
) values

  values (#{item.hashIndex,jdbcType=VARCHAR}, #{item.address,jdbcType=VARCHAR},
  #{item.amount,jdbcType=BIGINT}, #{item.lockTime,jdbcType=BIGINT}, #{item.spendTxHash,jdbcType=VARCHAR}
  )

ON DUPLICATE KEY UPDATE spend_tx_hash=VALUES(spend_tx_hash);
